package String;

public class StringReplaceingCharacter {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String s="      Welcome to Java";
		System.out.println(s.replace('a', 'd'));//s.replace is what to replace
		
		System.out.println(s.replace("Java", "Ankur"));//whole word " "
		//Single character ''
		
		

	}

}
